export class EditSamparkaryakarComponent {
    constructor(yuvakServices) {
        this.yuvakServices = yuvakServices;
        this.mandalId = 1;
        this.data = [{
                sId: 0,
                sName: '',
                yuvaks: [],
            }];
    }
    ngOnInit() {
        this.getAllYuvaks(this.mandalId);
    }
    async getAllYuvaks(mandalId) {
        await this.yuvakServices.getAllYuvakAndSK(mandalId)
            .subscribe(response => {
            this.data = response;
            console.log(this.data);
        });
    }
}
